$(document).ready(function(){

	$("#backtop").hide();
	
	$(function () {
		$(window).scroll(function () {
			if ($(this).scrollTop() > 100) {
				$('#backtop').fadeIn();
			} else {
				$('#backtop').fadeOut();
			}
		});

		$('#backtop').click(function () {
			$('body,html').animate({
				scrollTop: 0
			}, 900);
			return false;
		});
	});

